import {Form} from "react-bootstrap";
import {Button} from "react-bootstrap/";
import { Container } from "react-bootstrap";
import { useRef } from "react";

const AddNewProduct = (props) => {
    const titleInputRef = useRef();
    const imageInputRef = useRef();
    const textInputRef = useRef();
    const priceInputRef = useRef();

    const submitHandler = (event) => {
        event.preventDefault();

        const enteredTitle = titleInputRef.current.value;
        const enteredImage = imageInputRef.current.value;
        const enteredText = textInputRef.current.value;
        const enteredPrice = priceInputRef.current.value;

        const productData = {
        title: enteredTitle, 
        image:enteredImage,
        text: enteredText,
        price:enteredPrice,
    };

    props.onAddProduct(productData)
    }


    return (
      <Container fluid="sm">
      <Form onSubmit={submitHandler}>
        <Form.Group className="mb-3" controlId="enteredTitle">
          <Form.Label>Product title</Form.Label>
          <Form.Control as="textarea" rows={1}  ref={titleInputRef}/>
        </Form.Group>
        {/* <Form.Group controlId="enteredImage" className="mb-3">
        <Form.Label>Default file input example</Form.Label>
        <Form.Control type="file" />
      </Form.Group> */}
        <Form.Group className="mb-3" controlId="enteredImage">
          <Form.Label>Product image</Form.Label>
          <Form.Control as="textarea" rows={3} ref={imageInputRef} />
        </Form.Group>
        <Form.Group className="mb-3" controlId="enteredText">
          <Form.Label>Product text</Form.Label>
          <Form.Control as="textarea" rows={3} ref={textInputRef}/>
        </Form.Group>
        <Form.Group className="mb-3" controlId="enteredPrice">
          <Form.Label>Product price</Form.Label>
          <Form.Control as="textarea" rows={1} ref={priceInputRef}/>
        </Form.Group>
        <Button >
            Add Product
        </Button>
      </Form>
      </Container>
  );
};

export default AddNewProduct;
