import { useState } from "react";
import { Button,Form,Card,Row,Col } from "react-bootstrap";

const CollectionDetails = () => {
    const [nameCollection, setNameCollection] = useState("");
  const [descriptionCollection, setDescriptionCollection] = useState("");
  const [topicCollection, setTopicCollection] = useState("");
  const [tagCollection, setTagCollection] = useState("");

    return ( 
        <Row>
        <Col>
          <Card>
            <Card.Title>Create New Collection Form</Card.Title>
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Collection Name</Form.Label>

                <Form.Control
                  type="text"
                  required
                  id="name"
                  placeholder="Collection name"
                  value={nameCollection}
                  onChange={(e)=>setNameCollection(e.prevent.default)}
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Collection Tag</Form.Label>
                <Form.Control
                  type="text"
                  required
                  id="tag"
                  placeholder="Select Tag"
                  value={tagCollection}
                  onChange={(e)=>setTagCollection(e.prevent.default)}
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Collection topic</Form.Label>
                <Form.Control
                  rows={3}
                  type="text"
                  required
                  id="topic"
                  placeholder="Collection Topic"
                  value={topicCollection}
                  onChange={(e)=>setTopicCollection(e.prevent.default)}
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Collection Description</Form.Label>
                <Form.Control
                  rows={3}
                  type="text"
                  required
                  id="description"
                  placeholder="Collection Description"
                  value={descriptionCollection}
                  onChange={(e)=>setDescriptionCollection(e.prevent.default)}
                />
              </Form.Group>
              <Button type="submit">Create Collection</Button>
            </Form>
          </Card>
        </Col>
      </Row>
     );
}

export default CollectionDetails;
