// Every user has its personal page where they can mange list of collections (create new, delete, or edit) — each collection in the list is a link to the collection page that contains table of items with sorting/filtering and capabilities to create new item, edit or delete existing item).
import { useEffect, useState } from "react";
import {
  Col,
  Container,
  Row,
  Card,
  Button,
  Form,
  InputGroup,
  FormControl,
  Dropdown,
  DropdownButton,
  Spinner,
} from "react-bootstrap";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getUserDetails } from "../Redux/Actions/UserActions";
import Error from "../components/ui/Error";

const UserPage = () => {
  const dispatch = useDispatch("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState();

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const userDetails = useSelector((state) => state.userDetails);
  const { error,loading,user } = userDetails;

  const userUpdateProfile = useSelector((state) => state.userUpdateProfile);
  const {loading: updatedLoading } = userUpdateProfile;

  useEffect(() => {
    dispatch(getUserDetails("profile"));
  }, [dispatch]);

  useEffect(() => {
    if (user) {
      setName(user.name);
      setEmail(user.email);
    }
  }, [dispatch, user]);

  const submitHandler = (e) => {

    e.preventdefault();
    if (password !== confirmPassword) {
      console.log("dupa")
    } else {
      console.log("match");
    }
  };
  return (
    <Container>
      <Row>
        <Col>
          <Button>Update Profile</Button>

          <Card>
          <Card.Title>Update Profile</Card.Title>
            {error && <Error variant={"alert-danger"}>{error}</Error>}
            {loading && <Spinner />}
            <Card.Body>
            <Form onSubmit={submitHandler}>
              <Form.Group className="mb-3">
                <Form.Label>Username </Form.Label>
                <Form.Control
                  type="text"
                  required
                  id="name"
                  placeholder="Change Nickname"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="email"
                  required
                  id="email"
                  placeholder="Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="text"
                  id="password"
                  required
                  placeholder="Change Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Confirm Password</Form.Label>
                <Form.Control
                  type="text"
                  id="confirmPassword"
                  required
                  placeholder="Confirm Password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                />
              </Form.Group>
              <Card.Body>
                <Button type="submit">Confirm Changes</Button>
              </Card.Body>
            </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col>
          <Card>
            <Card.Body>
              <Card.Title>The {userInfo.name}'s Collection List</Card.Title>
              <Link to="/collection">Collection Name 1(4)</Link>
              <Link to="/collection">Collection Name 2(4)</Link>
              <Link to="/collection">Collection Name3(4)</Link>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Row>
        <Col>
          <InputGroup className="mb-3">
            <FormControl
              placeholder="filter"
              //   onChange={(event) => setSearchString(event.target.value)}
            />
            <DropdownButton title="Title">
              <Dropdown.Item>Item 1</Dropdown.Item>
            </DropdownButton>
            <DropdownButton title="Title">
              <Dropdown.Item>Item 2</Dropdown.Item>
            </DropdownButton>
          </InputGroup>
        </Col>
      </Row>

      <Row>
        <Col>
          <Button>Create New Collection</Button>
          <Card>
            <Card.Title>Create New Collection Form</Card.Title>
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Collection Name</Form.Label>

                <Form.Control
                  type="text"
                  required
                  id="title"
                  placeholder="Collection name"
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Collection Tag</Form.Label>
                <Form.Control
                  type="url"
                  required
                  id="tag"
                  placeholder="Select Tag"
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Collection topic</Form.Label>
                <Form.Control
                  rows={3}
                  type="text"
                  required
                  id="text"
                  placeholder="Collection Topic"
                />
              </Form.Group>
              <Button type="submit">Create Collection</Button>
            </Form>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default UserPage;
