import { USER_LOGIN_REQUEST, USER_LOGIN_SUCCESS, USER_LOGIN_FAIL, USER_LOGOUT, USER_REGISTER_REQUEST, USER_REGISTER_FAIL, USER_REGISTER_SUCCESS } from "../Constants/UserConstants";
import axios from "axios";

//login
export const login = (email, password) => async (dispatch) => {
    try {
      dispatch({ type: USER_LOGIN_REQUEST });

      const config = {
        headers:{
            "Content-Type":"application/json",
        }
      }

      const { data } = await axios.post(
        `http://localhost:8000/api/users/login`,{email, password},config
      );

      dispatch({ type: USER_LOGIN_SUCCESS, payload: data });

      localStorage.setItem("userInfo", JSON.stringify(data))
    } catch (error) {
      dispatch({
        type: USER_LOGIN_FAIL,
        payload:
          console.error.response && error.response.data.message
            ? error.response.data.message
            : error.message,
      });
    }
  };

  //logout
  export const logout = () => (dispatch) => {
    localStorage.removeItem("userInfo")
    dispatch({type:USER_LOGOUT})
    document.location.href="/"
  }

  //register
export const register = (name, email, password) => async (dispatch) => {
  try {
    dispatch({ type: USER_REGISTER_REQUEST });

    const config = {
      headers:{
          "Content-Type":"application/json",
      }
    }

    const { data } = await axios.post(
      `http://localhost:8000/api/users/register`,{name, email, password},config
    );

    dispatch({ type: USER_REGISTER_SUCCESS, payload: data });

    localStorage.setItem("userInfo", JSON.stringify(data))
  } catch (error) {
    dispatch({
      type: USER_REGISTER_FAIL,
      payload:
        console.error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};
