// import ButtonGroup from "react-bootstrap/ButtonGroup";
import { Form, Row, Button, Card, Col } from "react-bootstrap";

const Comments = ({title}) => {
  return (
    <>
      <Row>
        <Col>
          <Card>
            <Card.Header>Comments:</Card.Header>
            <Card.Body>
              <Form.Group className="mb-3">
                <Form.Label>Comment Form</Form.Label>
                <Form.Control placeholder="Please add comment" disabled />
                <Button>Add Comment</Button>
              </Form.Group>
            </Card.Body>
            <Card.Subtitle>Comments for {title}:</Card.Subtitle>
            <Card.Body>

            </Card.Body>
          </Card>
        </Col>
      </Row>
    </>
  );
};

export default Comments;
