import { useState } from "react";
import { Container } from "react-bootstrap/";
import { useDispatch, useSelector,useNavigate } from "react-redux";
import { logout } from "../../Redux/Actions/UserActions";
import Header from "./Header";

function Layout(props) {
  let navigate = useNavigate()
  const [keyword, setKeyword] = useState();
  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const logoutHandler = () => {
    dispatch(logout());
  };

  const submitHandler = (e) => {
  e.preventDefault()

  if(keyword.trim()){
    navigate(`/serch/${keyword}`)
  } else {
    navigate('/')
  }
}

  return (
    <div>
      <header>
        <Header userInfo={userInfo} logoutHandler={logoutHandler} setKeword={setKeyword} keyword={keyword} submiteHandler={submitHandler}/>
      </header>

      <main>
        <Container variant className="mt-5" fluid="sm">
          {props.children}
        </Container>
      </main>
    </div>
  );
}

export default Layout;
