import { useEffect } from "react";
import { Col, Container, Row, Card, Table, Button } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link, useParams, useLocation } from "react-router-dom";

import { addToCollection,removeFromCollection } from "../Redux/Actions/CollectionActions";

const Collection = () => {
  const dispatch = useDispatch();
  const { search } = useLocation();

  const { id } = useParams();
  const productId = id;

  const qty = search ? Number(search.split("=")[1]) : 1;

  const collection = useSelector((state) => state.collection);
  const { collectionItems } = collection;

  const removeFromCollectionHandle = (productId) => {
    dispatch(removeFromCollection(productId))
  };

  const total = collectionItems
    .reduce((a, i) => a + i.qty * i.price, 0)
    .toFixed(2);
  useEffect(() => {
    if (productId) {
      dispatch(addToCollection(productId, qty));
    }
  }, [dispatch, productId, qty]);

  // function imageFormatter(cell, row){
  //   return "<img src='"+cell+"'/>" ;
  // }

  return (
    <Container>
      <Row>
        <Col>
          <Card>
            <Card.Body>
              <Card.Title> Products ({collectionItems.length})</Card.Title>
              {collectionItems.length === 0 ? (
                <>
                  <h1>Your collection is empty</h1>
                  <Button>Create new Collection</Button>
                </>
              ) : (
                <>
                  <Table striped bordered hover>
                    <thead>
                      <tr>
                        <th>#</th>
                        <th>Product Name</th>
                        <th>Qty</th>
                        <th>Price</th>
                        <th

                        // dataFormat={imageFormatter}
                        >
                          Collection image
                        </th>
                        <th>Edit</th>
                        <th>Remove</th>
                      </tr>
                    </thead>
                    {collectionItems.map((item) => (
                      <tbody key={item.productId}>
                        <tr>
                          <td>1</td>
                          <td>
                            <Link to={`/products/${productId}`}>Product 1</Link>
                          </td>
                          <td>
                            <select
                              value={item.qty}
                              onChange={(e) =>
                                dispatch(
                                  addToCollection(
                                    item.product,
                                    Number ( e.target.value)
                                  )
                                )
                              }
                            >
                              {[...Array(item.countInStock).keys()].map((p) => (
                                <option key={p + 1} value={p + 1}>
                                  {p + 1}
                                </option>
                              ))}
                            </select>
                          </td>
                          <td>{item.productId}</td>
                          <td>{item.price}$</td>
                          <td>{item.image}</td>
                          <td>Edit</td>
                          <td>
                            <Button
                              onClick={() => removeFromCollectionHandle(item.product)}
                            >
                              Remove
                            </Button>
                          </td>
                        </tr>
                      </tbody>
                    ))}
                  </Table>
                </>
              )}

              <Card.Title>
                {" "}
                Total:
                <Card.Text>{total}</Card.Text>
              </Card.Title>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Collection;
