import { useEffect } from "react";
import { Col, Container, Row, Card, Table } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { Link, useParams, useLocation } from "react-router-dom";
import { addToCollection } from "../Redux/Actions/CollectionActions";



const Collection = () => {
  const _id = useParams();
  const { search } = useLocation();
  const qty = search ? Number(search.split("=")[1]) : 1;

  const dispatch = useDispatch();

  useEffect(() => {
    if (_id) {
      dispatch(addToCollection(_id, qty));
    }
  }, [dispatch, _id, qty]);

  // function imageFormatter(cell, row){
  //   return "<img src='"+cell+"'/>" ;
  // }

  return (
    <Container>
      <Row>
        <Col>
          <Card>
            <Card.Body>
              <Card.Title>Total Products in Collection Name</Card.Title>

              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Product Name</th>
                    <th>Qty</th>
                    <th>Price</th>
                    <th

                    // dataFormat={imageFormatter}
                    >
                      Collection image
                    </th>
                    <th>Edit</th>
                    <th>Remove</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>1</td>
                    <td>
                      <Link to="#">Product 1</Link>
                    </td>
                    <td>1</td>
                    <td>29$</td>
                    <td>image</td>
                    <td>Edit</td>
                    <td>Remove</td>
                  </tr>
                  <tr>
                    <td>2</td>
                    <td>
                      <Link to="#">Product 2</Link>
                    </td>
                    <td>2</td>
                    <td>49$</td>
                    <td>image</td>
                    <td>Edit</td>
                    <td>Remove</td>
                  </tr>
                  <tr>
                    <td>2</td>
                    <td>
                      <Link to="#">Product 3</Link>
                    </td>
                    <td>1</td>
                    <td>49$</td>
                    <td>image</td>
                    <td>Edit</td>
                    <td>Remove</td>
                  </tr>
                </tbody>
              </Table>
              <Card.Title>
                {" "}
                Total:
                <Card.Text>300$</Card.Text>
              </Card.Title>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Collection;
