export const addToCollection = createAsyncThunk(
  "collection/addItem",
  async ({ _id, qty }) => {
    const { data } = await axios.get(
      `http://localhost:8000/api/products/${_id}`
    );
    return {
      product: data._id,
      title: data.title,
      image: data.image,
      text: data.text,
      price: data.price,
      countInStock: data.countInStock,
      qty,
    };

  }

);
