import {Row, Col, Card, } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";
import Error from "../components/ui/Error";
import Spiner from "../components/ui/Spiner";
import { getOrderDetails } from "../Redux/Actions/OrderActions";
const Order = () => {
    const dispatch = useDispatch()
    const { id } = useParams();
    const orderId = id;



    const orderDetails = useSelector((state) => state.orderDetails);
    const { order, loading, error } = orderDetails;

    useEffect(()=>{

        dispatch(getOrderDetails(orderId))

    },[dispatch,orderId])



    return (
        <>

        {
            loading ? ( <Spiner/>) : error ? 
            (<Error variant="alert-danger">{error}</Error> ) :
            (
                <>
                <h1>Tabelka z danymi</h1>
                <p>{order.user.name}</p>

                <Row>
              <Col xl={6} className={classes.productcolumn}>
                <Card key={product._id} className="productlist">
                  <Card.Img variant="top" src={product.image} />
                </Card>
              </Col>
              <Col xl={6} className={classes.productcolumn}>
                <Card>
                  <Card.Body>
                    <Card.Title>Collections of user: {order.user.name}</Card.Title>
                    <Card.Text>{product.text}</Card.Text>
                    <Card.Text>{product.price} $</Card.Text>

                  </Card.Body>
                </Card>
              </Col>
            </Row>




                </>
            )
        }



        </>
     );
}

export default Order;
