import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Error from "../components/ui/Error";
import { Button } from "react-bootstrap";
import { useState } from "react";

const PlaceOrder = () => {
    const [nameCollection, setNameCollection]=useState("")
    const [descriptionCollection, setDescriptionCollection]=useState("");
    const [topicCollection, setTopicCollection]=useState("");
    const [tagCollection, setTagCollection]=useState("");

    const dispatch = useDispatch()

    const collection = useSelector((state)=> state.collection)
    const userLogin = useSelector((state)=> state.userLogin)
    const {userInfo} = userLogin;


    const placeOrderHandler = (e) => {
        e.preventDefault()
    }

    const submitCollectionHandler = (e) => {
        e.preventDefault()
        dispatch(createOrder(nameCollection,descriptionCollection,topicCollection,tagCollection))
    }


    return (  
        <>
        <div>
        <h2>user</h2>
        <p>{userInfo.name}</p>
        <p>{userInfo.email}</p>
        </div>
        <div>
            <h2>Summary</h2>
            {
                collection.collectionItems.length === 0 ? (
                    <Error variant="alert-info">Collection is empty</Error>
                ) : (
                    <>
                    {collection.collectionItems.map((item)=>(
                        <div key={item._id}>
                            <Link to={`/products/${item.prduct}`}>{item.title}</Link>
                            <p>Quantity: {item.qty}</p>
                            <p>Quantity: {item.price}</p>
                        </div>
                    ))
                    }
                    </>
                )
            }
            <Form onSubmit={submitCollectionHandler}>
                <input value={nameCollection} onChange={(e)=> setNameCollection(e.target.value)}/>
                <input value={descriptionCollection} onChange={(e)=> setDescriptionCollection(e.target.value)}/>
                <input value={topicCollection} onChange={(e)=> setTopicCollection(e.target.value)}/>
                <input value={tagCollection} onChange={(e)=> setTagCollection(e.target.value)}/>
            </Form>

            {
                collection.collectionItems.length === 0 ? null : (

                    <Button onClick={placeOrderHandler}>Create Collection</Button>

                )
            }
            <Button>Back to Collection</Button>

        </div>
        </>
    );
}

export default PlaceOrder;
