import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import {productSingle} from "../Redux/Actions/ProductActions";

import { Col, Card, Container, Row } from "react-bootstrap";
import classes from "./ProductDetails.module.css";

const ProductDetails = () => {
  const [loadedProduct, setLoadedProdust]=useState({})
  const dispatch = useDispatch()
  const { _id } = useParams();


  const singleProduct = useSelector((state)=>state.singleProduct);
  const {loading, error, product} = singleProduct

  useEffect(() => {
   dispatch(singleProduct(_id));
  }, [_id, dispatch]);

  return (
    <>
      <Container>
        <Row>
          <Col xl={6} className={classes.productcolumn}>
            <Card key={loadedProduct._id} className="productlist">
              <Card.Img variant="top" src={product.image} />
            </Card>
          </Col>
          <Col xl={6} className={classes.productcolumn}>
            <Card>
              <Card.Body>
                <Card.Title>{product.title}</Card.Title>
                <Card.Text>{product.text}</Card.Text>
                <Card.Text>{product.price} $</Card.Text>
              </Card.Body>
              <Card.Body>
                <Card.Title>{product.reviews}</Card.Title>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default ProductDetails;
