import axios from "axios";
import { COLLECTION_CREATE_FAIL, COLLECTION_CREATE_REQUEST, COLLECTION_CREATE_SUCCESS } from "../Constants/CollectionConstants";
import {COLLECTION_ADD_ITEM, COLLECTION_REMOVE_ITEM} from "../Constants/ColletionConstants";

export const addToCollection = (_id, qty) => async (dispatch, getState) => {
  const { data } = await axios.get(`http://localhost:8000/api/products/${_id}`);


  dispatch({
    type: COLLECTION_ADD_ITEM,
    payload: {
      product: data._id,
      title: data.title,
      image: data.image,
      text: data.text,
      price: data.price,
      countInStock: data.countInStock,
      qty,
    },
  });
  localStorage.setItem("collectionItems", JSON.stringify(getState().collection.collectionItems)
  );
};

export  const removeFromCollection = (_id) => (dispatch,getState) =>{
dispatch({
  type: COLLECTION_REMOVE_ITEM,
  payload: _id,
})
localStorage.setItem("collectionItems", JSON.stringify(getState().collection.collectionItems));
}


//update
export const updateUserProfile = (user) => async(dispatch,getState) => {
  try {
    dispatch({ type: COLLECTION_CREATE_REQUEST });
    const {
      userLogin: { userInfo },
    } = getState();

    const config = {
      headers: {
        "Content-Type":"application/json",
        Authorization: `Bearer ${userInfo.token}`,
      },
    };

    const { data } = await axios.get(
      `http://localhost:8000/api/users/profile`,
      user,
      config
    );

    dispatch({ type: COLLECTION_CREATE_SUCCESS, payload: data });

    localStorage.setItem("userInfo",JSON.stringify(data))
  } catch (error) {
    dispatch({
      type: COLLECTION_CREATE_FAIL,
      payload:
       error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
}
