import { useState } from "react";

import {useDispatch, useSelector} from "react-redux";

import { saveCollectionDetails } from "../Redux/Actions/CollectionActions";

// import { useNavigate } from "react-router-dom";

import { Button, Form, Card, Row, Col } from "react-bootstrap";

const CollectionDetails = () => {




  const collection = useSelector((state)=> state.collection)
  const {collectionDetails} = collection;

  const [nameCollection, setNameCollection] = useState(collectionDetails.nameCollection);
  const [descriptionCollection, setDescriptionCollection] = useState(collectionDetails.descriptionCollection);
  const [topicCollection, setTopicCollection] = useState(collectionDetails.topicCollection);
  const [tagCollection, setTagCollection] = useState(CollectionDetails.tagCollection);

    const dispatch = useDispatch()

  const submitHandler = (e) => {
    dispatch(saveCollectionDetails({nameCollection, descriptionCollection, topicCollection, tagCollection}))

  };

  return (
    <Row>
      <Col>
        <Card>
          <Card.Title>Create New Collection Form</Card.Title>
          <Form onSubmit={submitHandler}>
            <Form.Group className="mb-3">
              <Form.Label>Collection Name</Form.Label>
              <Form.Control
                type="text"
                required
                id="name"
                placeholder="Collection name"
                value={nameCollection}
                onChange={(e) => setNameCollection(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Collection Tag</Form.Label>
              <Form.Control
                type="text"
                required
                id="tag"
                placeholder="Select Tag"
                value={tagCollection}
                onChange={(e) => setTagCollection(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Collection topic</Form.Label>
              <Form.Control

                type="text"
                required
                id="topic"
                placeholder="Collection Topic"
                value={topicCollection}
                onChange={(e) => setTopicCollection(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Collection Description</Form.Label>
              <Form.Control

                type="text"
                required
                id="description"
                placeholder="Collection Description"
                value={descriptionCollection}
                onChange={(e) => setDescriptionCollection(e.target.value)}
              />
            </Form.Group>
            <Button type="submit">Save Details</Button>
          </Form>
        </Card>
      </Col>
    </Row>
  );
};

export default CollectionDetails;
