import { ORDER_CREATE_RESET } from "../Constants/OrderConstants";
import { PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS,PRODUCT_LIST_FAIL, PRODUCT_DETAILS_REQUEST, PRODUCT_DETAILS_SUCCESS, PRODUCT_DETAILS_FAIL, PRODUCT_CREATE_REVIEW_REQUEST, PRODUCT_CREATE_REVIEW_SUCCESS, PRODUCT_CREATE_REVIEW_FAIL } from "../Constants/ProductConstant";

//All products
export const productListReducer = (state = { products: [] }, action) => {
  switch (action.type) {
    case PRODUCT_LIST_REQUEST:
      return { loading: true, products: [] };
    case PRODUCT_LIST_SUCCESS:
      return { loading: false, products: action.payload };
      case PRODUCT_LIST_FAIL:
        return { loading: false, error:action.payload };
    default:
    return state;
  }
};

//DETAILS product
export const productCreateReviewReducer = (state = {  }, action) => {
  switch (action.type) {
    case PRODUCT_CREATE_REVIEW_REQUEST:
      return { ...state,loading: true};
    case PRODUCT_CREATE_REVIEW_SUCCESS:
      return { loading: false, success:true };
      case PRODUCT_CREATE_REVIEW_FAIL:
        return { loading: false, error:action.payload };
        case ORDER_CREATE_RESET:
          return {  };
    default:
    return state;
  }
};
