import { useEffect } from "react";
import {useDispatch, useSelector} from "react-redux";
import {useNavigate, } from "react-router-dom";
import { createOrder } from "../Redux/Actions/OrderActions";
import { ORDER_CREATE_RESET } from "../Redux/Constants/OrderConstants";

const Order = () => {
    const dispatch = useDispatch()

    const collection = useSelector((state)=>state.collection)
    const userLogin = useSelector((state)=> state.useLogin);
    const {userInfo} = userLogin;

    const orderCreate = useSelector((state) => state.orderCreate);
    const {order, success, error} = orderCreate;




    // order.name

    // order.description

    // order.topic

    // order.tag


    const navigate = useNavigate()



    const orderHandler = (e) => {
        e.preventDefault()
        dispatch(
        createOrder({
            orderItems: collection.collectionItems, 
            name: collection.name, 
            description: collection.description,
            topic: collection.description,
            tag: collection.tag,
        })
        )
    }

    return (
        <>
        <h1>Collection Lists</h1>
        <h2>{userInfo.name}</h2>
        <ul>
            <li>{collection.collectionItems}</li>
        </ul>
        {collection.collectionItems.length === 0 ? null : (
        <button type="submit" onClick={orderHandler}>
            Create Order
        </button>
        )}
        {error && <p>{error}</p>}
        </> 

     );
}

export default Order;
