import { Row, Button } from "react-bootstrap";
import { Link } from "react-router-dom";

import Product from "./Product";

const ProductList = (props) => {
  return (
    <Row xs md={4} xl={6}>
      {props.products.map((product) => {
        const { id, title, price, image, text } = product;
        return (
          <>
            <Product
              key={id}
              title={title}
              image={image}
              text={text}
              price={price}
            />
            <Link to={`/products/${id}`}>
              <Button variant="primary">`Check details ${title}`</Button>
            </Link>
          </>
        );
      })}
    </Row>
  );
};

export default ProductList;
