import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import {product} from "../Redux/Actions/ProductActions";

import { Col, Card, Container, Row } from "react-bootstrap";
import classes from "./ProductDetails.module.css";

const ProductDetails = () => {
  const { _id } = useParams();
  const dispatch = useDispatch()

  const [loadedProduct, setLoadedProduct] = useState([]);



  useEffect(() => {
   dispatch(product(_id));
  }, [_id, dispatch]);
  return (
    <>
      <Container>
        <Row>
          <Col xl={6} className={classes.productcolumn}>
            <Card key={loadedProduct._id} className="productlist">
              <Card.Img variant="top" src={loadedProduct.image} />
            </Card>
          </Col>
          <Col xl={6} className={classes.productcolumn}>
            <Card>
              <Card.Body>
                <Card.Title>{loadedProduct.title}</Card.Title>
                <Card.Text>{loadedProduct.text}</Card.Text>
                <Card.Text>{loadedProduct.price} $</Card.Text>
              </Card.Body>
              <Card.Body>
                <Card.Title>{loadedProduct.reviews}</Card.Title>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default ProductDetails;
