import { PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS,PRODUCT_LIST_FAIL, PRODUCT_SINGLE_REQUEST, PRODUCT_SINGLE_SUCCESS, PRODUCT_SINGLE_FAIL } from "../ProductConstant";

//All products
export const productListReducer = (state = { products: [] }, action) => {
  switch (action.type) {
    case PRODUCT_LIST_REQUEST:
      return { loading: true, products: [] };
    case PRODUCT_LIST_SUCCESS:
      return { loading: false, products: action.payload };
      case PRODUCT_LIST_FAIL:
        return { loading: false, error:action.payload };
    default:
    return state;
  }
};

//single product
export const productSingleReducer = (state = { product: {reviews:[]} }, action) => {
  switch (action.type) {
    case PRODUCT_SINGLE_REQUEST:
      return { ...state,loading: true, products: [] };
    case PRODUCT_SINGLE_SUCCESS:
      return { loading: false, product: action.payload };
      case PRODUCT_SINGLE_FAIL:
        return { loading: false, error:action.payload };
    default:
    return state;
  }
};
