// import { HandThumbsUpFill, HandThumbsDownFill } from "react-bootstrap-icons";
// import ButtonGroup from "react-bootstrap/ButtonGroup";
import moment from "moment";
import { Form, Row, Button, Card, Col } from "react-bootstrap";
import Spiner from "../ui/Spiner";
import Error from "../ui/Error";

const Comments = ({title, review, comment, user, loading, error}) => {
  return (
    <>
      <Row>
        <Col>
          <Card>
            <Card.Header>Comments:</Card.Header>
            <Card.Body>
              {loading && <Spiner/>}
              {error && (<Error variant="alert-danger">{error}</Error> )}
              <Form.Group className="mb-3">
                <Form.Label>Comment Form</Form.Label>
                <Form.Control placeholder="Please add comment" disabled />
                <Button>Add Comment</Button>
              </Form.Group>
            </Card.Body>
            <Card.Title>Comments for {title}:</Card.Title>
            <Card key={review._id}>
              <Card.Subtitle>
                  {review.name}
              </Card.Subtitle>
              <Card.Text>{review.comment}</Card.Text>
              <Card.Text>{moment(review.createdAt).calendar()}</Card.Text>
            </Card>

            <Card.Body>

            </Card.Body>
          </Card>
        </Col>
      </Row>
    </>
  );
};

export default Comments;
