import { useState, useEffect } from "react";
import {useParams} from "react-router-dom";

import axios from "axios";

import { Col,Card } from "react-bootstrap";
import classes from "./ProductDetails.module.css";

const ProductDetails = ({ match }) => {
  const {id} = useParams()
  console.log({id})
  const [loadedProduct, setLoadedProduct] = useState({});

  useEffect(() => {
    const fetchProduct = async () => {
      console.log("elo");
      const { data } = await axios.get(
        `http://localhost:8000/api/products/${match.params._id}`
      );
      setLoadedProduct(data);
      console.log(data)
    };
    fetchProduct();
  }, [match.params.id]);
  return (
    <>
      <Col xs={12} className={classes.productcolumn}>
        <Card key={loadedProduct._id} className="productlist">
          <Card.Img variant="top" src={loadedProduct.image} />
          <Card.Body>
            <Card.Title>{loadedProduct.title}</Card.Title>
            <Card.Text>{loadedProduct.text}</Card.Text>
            <Card.Text>{loadedProduct.price} $</Card.Text>
          </Card.Body>
        </Card>
      </Col>
    </>
  );
};

export default ProductDetails;
