import { useEffect } from "react";
import {useDispatch, useSelector} from "react-redux";
import {useNavigate, } from "react-router-dom";
import { createCollection } from "../Redux/Actions/CollectionActions";
import { COLLECTION_CREATE_RESET } from "../Redux/Constants/CollectionConstants";

const CollectionLists = () => {
    const dispatch = useDispatch()

    const collection = useSelector((state) => state.collection);
    const userLogin = useSelector((state)=> state.useLogin);
    const {userInfo} = userLogin;


    const collectionCreate = useSelector((state) => state.collectionCreate)
    const {collection:CreatedColletion,success, error} = collectionCreate

    const navigate = useNavigate()

    useEffect(()=>{
        if(success){
            navigate(`/collection/${collection._id}`)
            dispatch({type:COLLECTION_CREATE_RESET})
        }
    },[navigate, dispatch, success, collection])

    const collectionHandler = (e) => {
        e.preventDefault()

        createCollection({
            collectionItems: collection.collectionItems, 
            name: collection.name, 
            description: collection.description,
            topic: collection.description,
            tag: collection.tag,
        })
    }

    return ( 
        <h1>Collection Lists</h1>
     );
}

export default CollectionLists;
